import React, { useState } from "react";

const Handle = ({ type, position, id, className, style }) => {
  const positionStyle = position === "left" ? { left: -4 } : { right: -4 };
  return (
    <div
      id={id}
      className={`absolute w-3 h-3 rounded-full ${className}`}
      style={{ ...positionStyle, top: "50%", transform: "translateY(-50%)", ...style }}
    />
  );
};

export const BaseNode = ({
  id,
  title,
  inputs = [],
  leftHandles = [],
  rightHandles = [],
  data = {},
  className = "",
  slotTop = null,
  slotBottom = null,
}) => {
  const [state, setState] = useState(data);

  const handleChange = (key) => (e) => {
    setState({
      ...state,
      [key]: e.target.value,
    });
  };

  return (
    <div className=" p-16 bg-green-500">    
    <div className={`relative w-64 bg-white rounded-lg shadow-md overflow-hidden ${className}`}>
      <div className="bg-gray-100 px-4 py-2 border-b border-gray-200">
        <h3 className="text-lg font-semibold text-gray-800">{title}</h3>
      </div>
      {slotTop && <div className="px-4 py-2">{slotTop}</div>}
      <div className="px-4 py-2 space-y-2">
        {inputs.map(({ label, key, type = "text", options }) => (
          <label key={key} className="block">
            <span className="text-sm font-medium text-gray-700">{label}:</span>
            {type === "select" ? (
              <select
                value={state[key] || ""}
                onChange={handleChange(key)}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
              >
                {options.map((option) => (
                  <option value={option} key={option}>
                    {option}
                  </option>
                ))}
              </select>
            ) : (
              <input
                type={type}
                value={state[key] || ""}
                onChange={handleChange(key)}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
              />
            )}
          </label>
        ))}
      </div>
      {slotBottom && <div className="px-4 py-2">{slotBottom}</div>}
      {leftHandles.map((handle) => (
        <Handle
          key={handle.id}
          type="target"
          position="left"
          id={handle.id}
          className="bg-blue-500"
          style={handle.style}
        />
      ))}
      {rightHandles.map((handle) => (
        <Handle
          key={handle.id}
          type="source"
          position="right"
          id={handle.id}
          className="bg-green-500"
          style={handle.style}
        />
      ))}
    </div>
    </div>
  );
};
